import teemimport ctypes imgA = teem.nrrdNew()teem.nrrdLoad(imgA, "head.png", None)imgB = teem.nrrdNew()rsmc = teem.nrrdResampleContextNew();teem.nrrdResampleDefaultCenterSet(rsmc, teem.nrrdCenterCell);teem.nrrdResampleNrrdSet(rsmc, imgA);teem.nrrdResampleSamplesSet(rsmc, 0, 10*imgA.contents.axis[0].size)teem.nrrdResampleRangeFullSet(rsmc, 0)teem.nrrdResampleSamplesSet(rsmc, 1, 10*imgA.contents.axis[1].size)teem.nrrdResampleRangeFullSet(rsmc, 1)kparm =  (ctypes.c_double * teem.NRRD_KERNEL_PARMS_NUM)()kparm[0] = 1teem.nrrdResampleKernelSet(rsmc, 0, teem.nrrdKernelBox, kparm)teem.nrrdResampleKernelSet(rsmc, 1, teem.nrrdKernelBox, kparm)teem.nrrdResampleExecute(rsmc, imgB)teem.nrrdSave("head-box.png", imgB, None)kparm[0] = 1teem.nrrdResampleKernelSet(rsmc, 0, teem.nrrdKernelTent, kparm)teem.nrrdResampleKernelSet(rsmc, 1, teem.nrrdKernelTent, kparm)teem.nrrdResampleExecute(rsmc, imgB)teem.nrrdSave("head-tent.png", imgB, None)kparm[0] = 1.0kparm[1] = 0.0kparm[2] = 0.5teem.nrrdResampleKernelSet(rsmc, 0, teem.nrrdKernelBCCubic, kparm)teem.nrrdResampleKernelSet(rsmc, 1, teem.nrrdKernelBCCubic, kparm)teem.nrrdResampleExecute(rsmc, imgB)teem.nrrdSave("head-ctmr.png", imgB, None)kparm[0] = 1.0kparm[1] = 1.0kparm[2] = 0.0teem.nrrdResampleKernelSet(rsmc, 0, teem.nrrdKernelBCCubic, kparm)teem.nrrdResampleKernelSet(rsmc, 1, teem.nrrdKernelBCCubic, kparm)teem.nrrdResampleExecute(rsmc, imgB)teem.nrrdSave("head-bspln3.png", imgB, None)kparm[0] = 2.0kparm[1] = 5.0teem.nrrdResampleKernelSet(rsmc, 0, teem.nrrdKernelGaussian, kparm)teem.nrrdResampleKernelSet(rsmc, 1, teem.nrrdKernelGaussian, kparm)teem.nrrdResampleExecute(rsmc, imgB)teem.nrrdSave("head-gauss.png", imgB, None)kparm[0] = 1.0kparm[1] = 10teem.nrrdResampleKernelSet(rsmc, 0, teem.nrrdKernelHann, kparm)teem.nrrdResampleKernelSet(rsmc, 1, teem.nrrdKernelHann, kparm)teem.nrrdResampleExecute(rsmc, imgB)teem.nrrdSave("head-hann.png", imgB, None)# cleanupteem.nrrdResampleContextNix(rsmc)teem.nrrdNuke(imgA)teem.nrrdNuke(imgB)